namespace jsidea.build.Usage {
    export interface ITypeScriptOptions extends ts.CompilerOptions {

    }
    class TypeScriptUsage implements IUsage {

        private _symbols: ISymbol[];
        private _stack: ts.ModuleDeclaration[];
        private _program: ts.Program;
        private _file: IFile;

        public apply(files: IFile[], options?: ITypeScriptOptions): IUsageResult {
            options = options || { noLib: true };
            this._stack = [];
            this._symbols = [];

            //create program
            var host = new CompilerHost(files);
            var names: string[] = [];
            for (var f of files)
                names.push(f.name);
            this._program = ts.createProgram(names, options, host);
            
            //process exports
            this._program.getSourceFiles().forEach(node => {
                this._file = host.find(node.fileName);
                if (node.fileName.indexOf(".d.ts") === -1)
                    this.processExport(node);
            });
            
            //process imports
            this._stack = [];
            this._program.getSourceFiles().forEach(node => {
                this._file = host.find(node.fileName);
                if (node.fileName.indexOf(".d.ts") === -1)
                    this.processImport(node);
            });

            return {
                symbols: this._symbols
            };
        }

        private getFullName(node: ts.Node): string {
            var symbol: ts.Symbol = this._program.getTypeChecker().getSymbolAtLocation(node);
            if (!symbol)
                symbol = (<any>node).symbol;
            if (!symbol)
                return "";
            return this._program.getTypeChecker().getFullyQualifiedName(symbol);
        }

        private getName(node: ts.Node): string {
            var path = this.getPath(node);
            return path ? path[path.length - 1] : "";
        }

        private getPath(node: ts.Node): string[] {
            var qname = this.getFullName(node);
            if (qname)
                return qname.split(".");
            return null;
        }

        private processExport(node: ts.Node): void {
            if (node.kind == ts.SyntaxKind.ModuleDeclaration)
                this._stack.push(<ts.ModuleDeclaration>node);

            switch (node.kind) {
                case ts.SyntaxKind.ModuleDeclaration:
                case ts.SyntaxKind.ClassDeclaration:
                case ts.SyntaxKind.InterfaceDeclaration:
                case ts.SyntaxKind.EnumDeclaration:
                case ts.SyntaxKind.VariableDeclaration:
                case ts.SyntaxKind.FunctionDeclaration:
                    this.extractExportNames(node);
            }

            var skipChildren = node.kind == ts.SyntaxKind.ClassDeclaration;
            if (!skipChildren)
                ts.forEachChild(node, node => this.processExport(node));

            if (node.kind == ts.SyntaxKind.ModuleDeclaration)
                this._stack.pop();
        }

        private extractExportNames(node: ts.Node) {
            var isNodeExported = false;
            if (this._stack.length > 0) {
                var name = this.getName(node);
                var lastModule: any = this._stack[this._stack.length - 1];
                isNodeExported = lastModule.symbol ? lastModule.symbol.exports.hasOwnProperty(name) : false;
            }
            if (isNodeExported) {
                var fullName = this.getFullName(node);
                this.addExport(fullName, node.kind);
            }
        }

        private addExport(fullName: string, kind: any): void {
            var s = this.getSymbolByName(fullName);
            if (!s)
                this._symbols.push({ fullName: fullName, kind: kind, file: this._file, imports: [] });
        }

        private processImport(node: ts.Node): void {
            if (node.kind == ts.SyntaxKind.ModuleDeclaration)
                this._stack.push(<ts.ModuleDeclaration>node);
            
            //TODO: remove of integrate some filter option
            switch (node.kind) {
//                case ts.SyntaxKind.Identifier:
//                case ts.SyntaxKind.ExtendsKeyword:
//                case ts.SyntaxKind.ClassKeyword:
//                case ts.SyntaxKind.ClassExpression:
//                case ts.SyntaxKind.ClassDeclaration:
//                case ts.SyntaxKind.PropertyAccessExpression:
                default:
                    this.extractImportNames(node);
            }

            ts.forEachChild(node, node => this.processImport(node));

            if (node.kind == ts.SyntaxKind.ModuleDeclaration)
                this._stack.pop();
        }

        private extractImportNames(node: ts.Node): void {
            var path = this.getPath(node);
            if (node.kind == ts.SyntaxKind.ExpressionWithTypeArguments) {
                var exp = <ts.ExpressionWithTypeArguments>node;
                var signature = exp.expression.getText();
                var stack = this._stack.map((a) => { return a.name.getText(); });
                var mod = "";
                this.addImport(signature);
                if (signature && stack.length > 0) {
                    for (var modName of stack) {
                        mod += (mod ? "." : "") + modName;
                        if (mod) {
                            this.addImport(mod + "." + signature);
                        }
                    }
                }
            }
            else if (path) {
                var fullName: string = "";
                var l = path.length;
                for (var i = 0; i < l; ++i) {
                    fullName += (fullName ? "." : "") + path[i];
                    this.addImport(fullName);
                }
            }
        }

        private addImport(fullName: string): void {
            var s = this.getSymbolByName(fullName);
            if (s) {
                var file = this._file;
                for (var e of this._symbols)
                    if (e != s && e.file == file && e.imports.indexOf(s) === -1)
                        e.imports.push(s);
            }
        }

        private getSymbolByName(fullName: string): ISymbol {
            for (var s of this._symbols)
                if (s.fullName == fullName)
                    return s;
            return null;
        }
    }

    export var TYPESCRIPT = new TypeScriptUsage();
}

namespace jsidea.display {
    export class Graphics {

        private static _instance: Graphics = new Graphics();
        public static get(ctx: CanvasRenderingContext2D): Graphics {
            Graphics._instance._ctx = ctx;
            return Graphics._instance;
        }

        private _ctx: CanvasRenderingContext2D;

        public bounds(element: HTMLElement, boxModel?: layout.IBoxModel): Graphics {
            var ctx = this._ctx;
            var can: HTMLElement = ctx.canvas;
            boxModel = boxModel || layout.BoxModel.BORDER;

            var from = layout.Transform.create(element);
            var to = layout.Transform.create(can);
            var quad = from.size.bounds(boxModel).toQuad();
            quad = from.localToLocalQuad(to, quad, null, layout.BoxModel.CANVAS, quad);
            this.quad(quad);

            return this;
        }

        public strokeColor(color: string): Graphics {
            var ctx = this._ctx;
            ctx.strokeStyle = color;
            return this;
        }
        
        public lineWidth(lineWidth: number): Graphics {
            var ctx = this._ctx;
            ctx.lineWidth = lineWidth;
            return this;
        }

        public stroke(color?: string, lineWidth?: number): Graphics {
            var ctx = this._ctx;
            ctx.closePath();
            if (color !== undefined)
                this.strokeColor(color);
            if (lineWidth !== undefined)
                this.lineWidth(lineWidth);
            ctx.stroke();
            ctx.beginPath();
            return this;
        }

        public cross(x: number, y: number, size: number): Graphics {
            var ctx = this._ctx;

            ctx.moveTo(x + size, y);
            ctx.lineTo(x - size, y);
            ctx.moveTo(x, y + size);
            ctx.lineTo(x, y - size);
            return this;
        }

        public quad(quad: geom.Quad): Graphics {
            var ctx = this._ctx;

            ctx.moveTo(quad.p1.x, quad.p1.y);
            ctx.lineTo(quad.p2.x, quad.p2.y);
            ctx.lineTo(quad.p3.x, quad.p3.y);
            ctx.lineTo(quad.p4.x, quad.p4.y);
            ctx.lineTo(quad.p1.x, quad.p1.y);
            return this;
        }

        public rect(box: geom.Rect2D): Graphics {
            var ctx = this._ctx;

            ctx.moveTo(box.x, box.y);
            ctx.lineTo(box.x + box.width, box.y);
            ctx.lineTo(box.x + box.width, box.y + box.height);
            ctx.lineTo(box.x, box.y + box.height);
            ctx.lineTo(box.x, box.y);
            return this;
        }

        public clear(): Graphics {
            var ctx = this._ctx;
            ctx.closePath();
            ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
            ctx.beginPath();
            return this;
        }
    }
}

namespace jsidea.events {
    export class EventDispatcher implements IEventDispatcher {
        
        private _listener = new model.Dictonary<string, IEventListener[]>();
        private _scope: any;

        constructor(scope: any = null) {
            this._scope = scope === null ? this : scope;
        }

        public addEventListener(
            type: string,
            listener: IEventListener,
            useCapture: boolean = false): void {

            var listeners = this.getListeners(type, useCapture);
            var index = listeners.indexOf(listener);
            if (index === -1)
                listeners.push(listener);
        }

        public removeEventListener(
            type: string,
            listener: IEventListener,
            useCapture: boolean = false): void {

            var listeners = this.getListeners(type, useCapture);
            var index = listeners.indexOf(listener);
            if (index !== -1)
                listeners.splice(index, 1);
        }

        public dispatchEvent(
            event: Event): boolean {

            var listeners = this.getListeners(event.type, false);
            for (var i = 0; i < listeners.length; i++)
                listeners[i].apply(this._scope, event);
            return !event.defaultPrevented;
        }

        public getListeners(
            type: string,
            useCapture: boolean = false): IEventListener[] {
            var key = type + (useCapture ? '1' : '0');
            var listener = this._listener.getValue(key);
            if (!listener)
                return this._listener.setValue(key, []);
            return listener;
        }

        public dispose(): void {
            this._listener = null;
            this._scope = null;
        }
    }
}

