            this.m12 = m11 * BETA + this.m12 * THETA;
            this.m21 = m21 * THETA - this.m22 * BETA;
            this.m22 = m21 * BETA + this.m22 * THETA;
            this.m31 = m31 * THETA - this.m32 * BETA;
            this.m32 = m31 * BETA + this.m32 * THETA;
