        public getSkew(ret: Point3D = new Point3D()): Point3D {
            ret.x = this.m14 * math.Number.RAD_TO_DEG;
            ret.y = this.m24 * math.Number.RAD_TO_DEG;
            ret.z = this.m34 * math.Number.RAD_TO_DEG;
            ret.w = this.m44;
            return ret;
