        /**
        * Get the decomposed rotation-angles in degree (xyz-order).
        * @param ret Optional buffer.
        * @return The rotation-point.
        */
        public getRotation(ret: Point3D = new Point3D()): Point3D {
            var m = this.getRotationMatrix(Matrix3D._MATRIX3D);
            ret.y = -Math.asin(math.Number.clamp(m.m13, -1, 1)) * math.Number.RAD_TO_DEG;
            if (Math.abs(this.m13) < 0.99999) {
                ret.x = Math.atan2(-m.m23, m.m33) * math.Number.RAD_TO_DEG;
                ret.z = Math.atan2(-m.m12, m.m11) * math.Number.RAD_TO_DEG;
            } else {
                ret.x = Math.atan2(m.m32, m.m22) * math.Number.RAD_TO_DEG;
                ret.z = 0;
            }
            return ret;
        }
        
        /**
        * Sets the given rotation-angles in degree (xyz-order).
        * SOURCE: https://github.com/kamicane/matrix3d/blob/master/lib/Matrix3d.js
        * @param scale The rotation-angles.
        * @return this-chained.
        */
        public setRotation(euler: IPoint3DValue): Matrix3D {
            var x = euler.x * math.Number.DEG_TO_RAD;
            var y = euler.y * math.Number.DEG_TO_RAD;
            var z = euler.z * math.Number.DEG_TO_RAD;
            var a = Math.cos(x);
            var b = Math.sin(x);
            var c = Math.cos(y);
            var d = Math.sin(y);
            var e = Math.cos(z);
            var f = Math.sin(z);

            var ae = a * e;
            var af = a * f;
            var be = b * e;
            var bf = b * f;

            this.m11 = c * e;
            this.m21 = -c * f;
            this.m31 = d;
            this.m12 = af + be * d;
            this.m22 = ae - bf * d;
            this.m32 = -b * c;
            this.m13 = bf - ae * d;
            this.m23 = be + af * d;
            this.m33 = a * c;

            return this;
        }

        public static makeRotation(euler: IPoint3DValue): Matrix3D {
            var ret = new Matrix3D();
            ret.setRotation(euler);
            return ret;
        }

        public appendRotation(euler: IPoint3DValue): Matrix3D {
            return this.append(Matrix3D.makeRotation(euler));
        }

        public prependRotation(euler: IPoint3DValue): Matrix3D {
            return this.prepend(Matrix3D.makeRotation(euler));
        }

        public getRotationMatrix(ret: Matrix3D = new Matrix3D()): Matrix3D {
            ret.identity();

            var tmp = new Point3D();
            var scaleX = 1 / tmp.setTo(this.m11, this.m12, this.m13).length();
            var scaleY = 1 / tmp.setTo(this.m21, this.m22, this.m23).length();
            var scaleZ = 1 / tmp.setTo(this.m31, this.m32, this.m33).length();

            ret.m11 = this.m11 * scaleX;
            ret.m12 = this.m12 * scaleX;
            ret.m13 = this.m13 * scaleX;
            ret.m21 = this.m21 * scaleY;
            ret.m22 = this.m22 * scaleY;
            ret.m23 = this.m23 * scaleY;
            ret.m31 = this.m31 * scaleZ;
            ret.m32 = this.m32 * scaleZ;
            ret.m33 = this.m33 * scaleZ;
            return ret;
        }
        
        /**
       * Creates a new perspective-matrix.
       * @param perspective The perspective.
       * @return The new perspective-matrix.
       */
        public static makePerspective(perspective: number, ret = new Matrix3D()): Matrix3D {
            ret.identity();
            ret.m34 = perspective ? -(1 / perspective) : 0;
            return ret;
        }

        /**
        * Appends perspective.
        * @param perspective The perspective (focal length).
        * @return this-chained.
        */
        public appendPerspective(perspective: number): Matrix3D {
            if (!perspective)
                return this;
            return this.append(Matrix3D.makePerspective(perspective, Matrix3D._MATRIX3D));
        }

        /**
        * Prepends perspective.
        * @param perspective The perspective (focal length).
        * @return this-chained.
        */
        public prependPerspective(perspective: number): Matrix3D {
            return this.prepend(Matrix3D.makePerspective(perspective, Matrix3D._MATRIX3D));
        }

        public getPerspective(): number {
            return this.m34 ? - (1 / this.m34) : 0;
        }

        public setPerspective(perspective: number): Matrix3D {
            this.m34 = perspective ? -(1 / perspective) : 0;
            return this;
        }

        public compose(trans: IComposition3D): Matrix3D {
            this.identity();
            if (trans.scale.x != 1 || trans.scale.y != 1 || trans.scale.z != 1)
                this.appendScale(trans.scale);
            if (trans.skew.x || trans.skew.y || trans.skew.z)
                this.appendSkew(trans.skew);
            if (trans.rotation.x != 0 || trans.rotation.y != 0 || trans.rotation.z != 0)
                this.appendRotation(trans.rotation);
            if (trans.position.x || trans.position.y || trans.position.z)
                this.appendPosition(trans.position);
            if (trans.perspective)
                this.appendPerspective(trans.perspective);
            return this;
        }

        public decompose(ret: IComposition3D = null): IComposition3D {
            if (ret) {
                ret.perspective = this.getPerspective();
                ret.position = this.getPosition(ret.position);
                ret.skew = this.getSkew(ret.skew);
                ret.scale = this.getScale(ret.scale);
                ret.rotation = this.getRotation(ret.rotation);
                return ret;
            }
            return {
                perspective: this.getPerspective(),
                position: this.getPosition(),
                skew: this.getSkew(),
                scale: this.getScale(),
                rotation: this.getRotation()
            };
        }
        
        /**
        * Inverts the matrix.
        * -> based on http://www.euclideanspace.com/maths/algebra/matrix/functions/inverse/fourD/index.htm
        * -> based on https://github.com/mrdoob/three.js/blob/master/src/math/Matrix4.js
        * @return this-chained.
        */
        public invert(target?: Matrix3D): Matrix3D {
            target = target || this;
            var data: number[] = [];

            var n11 = this.m11, n12 = this.m12, n13 = this.m13, n14 = this.m14;
            var n21 = this.m21, n22 = this.m22, n23 = this.m23, n24 = this.m24;
            var n31 = this.m31, n32 = this.m32, n33 = this.m33, n34 = this.m34;
            var n41 = this.m41, n42 = this.m42, n43 = this.m43, n44 = this.m44;

            data[0] = n23 * n34 * n42 - n24 * n33 * n42 + n24 * n32 * n43 - n22 * n34 * n43 - n23 * n32 * n44 + n22 * n33 * n44;
            data[1] = n14 * n33 * n42 - n13 * n34 * n42 - n14 * n32 * n43 + n12 * n34 * n43 + n13 * n32 * n44 - n12 * n33 * n44;
            data[2] = n13 * n24 * n42 - n14 * n23 * n42 + n14 * n22 * n43 - n12 * n24 * n43 - n13 * n22 * n44 + n12 * n23 * n44;
            data[3] = n14 * n23 * n32 - n13 * n24 * n32 - n14 * n22 * n33 + n12 * n24 * n33 + n13 * n22 * n34 - n12 * n23 * n34;

            var det = n11 * data[0] + n21 * data[1] + n31 * data[2] + n41 * data[3];
            if (det == 0) {
                console.warn("Can't invert matrix, determinant is 0");
                return this;
            }

            data[4] = n24 * n33 * n41 - n23 * n34 * n41 - n24 * n31 * n43 + n21 * n34 * n43 + n23 * n31 * n44 - n21 * n33 * n44;
            data[5] = n13 * n34 * n41 - n14 * n33 * n41 + n14 * n31 * n43 - n11 * n34 * n43 - n13 * n31 * n44 + n11 * n33 * n44;
            data[6] = n14 * n23 * n41 - n13 * n24 * n41 - n14 * n21 * n43 + n11 * n24 * n43 + n13 * n21 * n44 - n11 * n23 * n44;
            data[7] = n13 * n24 * n31 - n14 * n23 * n31 + n14 * n21 * n33 - n11 * n24 * n33 - n13 * n21 * n34 + n11 * n23 * n34;
            data[8] = n22 * n34 * n41 - n24 * n32 * n41 + n24 * n31 * n42 - n21 * n34 * n42 - n22 * n31 * n44 + n21 * n32 * n44;
            data[9] = n14 * n32 * n41 - n12 * n34 * n41 - n14 * n31 * n42 + n11 * n34 * n42 + n12 * n31 * n44 - n11 * n32 * n44;
            data[10] = n12 * n24 * n41 - n14 * n22 * n41 + n14 * n21 * n42 - n11 * n24 * n42 - n12 * n21 * n44 + n11 * n22 * n44;
            data[11] = n14 * n22 * n31 - n12 * n24 * n31 - n14 * n21 * n32 + n11 * n24 * n32 + n12 * n21 * n34 - n11 * n22 * n34;
            data[12] = n23 * n32 * n41 - n22 * n33 * n41 - n23 * n31 * n42 + n21 * n33 * n42 + n22 * n31 * n43 - n21 * n32 * n43;
            data[13] = n12 * n33 * n41 - n13 * n32 * n41 + n13 * n31 * n42 - n11 * n33 * n42 - n12 * n31 * n43 + n11 * n32 * n43;
            data[14] = n13 * n22 * n41 - n12 * n23 * n41 - n13 * n21 * n42 + n11 * n23 * n42 + n12 * n21 * n43 - n11 * n22 * n43;
            data[15] = n12 * n23 * n31 - n13 * n22 * n31 + n13 * n21 * n32 - n11 * n23 * n32 - n12 * n21 * n33 + n11 * n22 * n33;

            target.setData(data);
            target.scalar(1 / det);
            return target;
        }

        public getCSS2D(fractionalDigits: number = 6): string {
            return "matrix("
                + this.m11.toFixed(fractionalDigits) + ","
                + this.m12.toFixed(fractionalDigits) + ","
                + this.m21.toFixed(fractionalDigits) + ","
                + this.m22.toFixed(fractionalDigits) + ","
                + this.m41.toFixed(fractionalDigits) + ","
                + this.m42.toFixed(fractionalDigits) + ")";
        }

        public getCSS(fractionalDigits: number = 6): string {
            return "matrix3d("
                + this.m11.toFixed(fractionalDigits) + ","
                + this.m12.toFixed(fractionalDigits) + ","
                + this.m13.toFixed(fractionalDigits) + ","
                + this.m14.toFixed(fractionalDigits) + ","
                + this.m21.toFixed(fractionalDigits) + ","
                + this.m22.toFixed(fractionalDigits) + ","
                + this.m23.toFixed(fractionalDigits) + ","
                + this.m24.toFixed(fractionalDigits) + ","
                + this.m31.toFixed(fractionalDigits) + ","
                + this.m32.toFixed(fractionalDigits) + ","
                + this.m33.toFixed(fractionalDigits) + ","
                + this.m34.toFixed(fractionalDigits) + ","
                + this.m41.toFixed(fractionalDigits) + ","
                + this.m42.toFixed(fractionalDigits) + ","
                + this.m43.toFixed(fractionalDigits) + ","
                + this.m44.toFixed(fractionalDigits) + ")";
        }

        public setCSS(cssString: string): Matrix3D {
            if (!cssString || cssString == "none")
                return this.identity();
            var trans: any = cssString.replace("matrix3d(", "").replace("matrix(", "").replace(")", "").split(",");
            var l = trans.length;
            for (var i = 0; i < l; ++i)
                trans[i] = math.Number.parse(trans[i], 0);
            return this.setData(trans);
        }

        public appendCSS(cssString: string, force2D: boolean = false): Matrix3D {
            if (!cssString || cssString == "none")
                return this;
            if (force2D && cssString.indexOf("matrix3d") >= 0)
                return this.append(Matrix3D._MATRIX3D.setCSS(cssString).flatten());
            return this.append(Matrix3D._MATRIX3D.setCSS(cssString));
        }

        public prependCSS(cssString: string, force2D: boolean = false): Matrix3D {
            if (!cssString || cssString == "none")
                return this;
            if (force2D && cssString.indexOf("matrix3d") >= 0)
                return this.prepend(Matrix3D._MATRIX3D.setCSS(cssString).flatten());
            return this.prepend(Matrix3D._MATRIX3D.setCSS(cssString));
        }

        public flatten(): Matrix3D {
            this.m31 = 0;
            this.m32 = 0;
            this.m33 = 1;
            this.m34 = 0;
            this.m44 = 1;
            this.m14 = 0;
            this.m24 = 0;
            this.m43 = 0;
            return this;
        }

        public is2D(): boolean {
            return (this.m31 == 0 && this.m32 == 0 && this.m33 == 1 && this.m34 == 0 && this.m43 == 0 && this.m44 == 1);
        }

        public is3D(): boolean {
            return !this.is2D();
        }

        public bounds(x: number, y: number, width: number, height: number, ret = new geom.Rect2D()): geom.Rect2D {
            var ptA = new geom.Point3D(x, y);
            var ptB = new geom.Point3D(x + width, y);
            var ptC = new geom.Point3D(x + width, y + height);
            var ptD = new geom.Point3D(x, y + height);

            this.project(ptA, ptA);
            this.project(ptB, ptB);
            this.project(ptC, ptC);
            this.project(ptD, ptD);

            var x = Math.min(ptA.x, ptB.x, ptC.x, ptD.x);
            var y = Math.min(ptA.y, ptB.y, ptC.y, ptD.y);
            var width = Math.max(ptA.x, ptB.x, ptC.x, ptD.x) - x;
            var height = Math.max(ptA.y, ptB.y, ptC.y, ptD.y) - y;

            return ret.setTo(x, y, width, height);
        }

        public static multiply(a: IMatrix3DValue, b: IMatrix3DValue, ret: Matrix3D = new Matrix3D()): Matrix3D {
            var m11 = a.m11 * b.m11 + a.m12 * b.m21 + a.m13 * b.m31 + a.m14 * b.m41;
            var m12 = a.m11 * b.m12 + a.m12 * b.m22 + a.m13 * b.m32 + a.m14 * b.m42;
            var m13 = a.m11 * b.m13 + a.m12 * b.m23 + a.m13 * b.m33 + a.m14 * b.m43;
            var m14 = a.m11 * b.m14 + a.m12 * b.m24 + a.m13 * b.m34 + a.m14 * b.m44;
            var m21 = a.m21 * b.m11 + a.m22 * b.m21 + a.m23 * b.m31 + a.m24 * b.m41;
            var m22 = a.m21 * b.m12 + a.m22 * b.m22 + a.m23 * b.m32 + a.m24 * b.m42;
            var m23 = a.m21 * b.m13 + a.m22 * b.m23 + a.m23 * b.m33 + a.m24 * b.m43;
            var m24 = a.m21 * b.m14 + a.m22 * b.m24 + a.m23 * b.m34 + a.m24 * b.m44;
            var m31 = a.m31 * b.m11 + a.m32 * b.m21 + a.m33 * b.m31 + a.m34 * b.m41;
            var m32 = a.m31 * b.m12 + a.m32 * b.m22 + a.m33 * b.m32 + a.m34 * b.m42;
            var m33 = a.m31 * b.m13 + a.m32 * b.m23 + a.m33 * b.m33 + a.m34 * b.m43;
            var m34 = a.m31 * b.m14 + a.m32 * b.m24 + a.m33 * b.m34 + a.m34 * b.m44;
            var m41 = a.m41 * b.m11 + a.m42 * b.m21 + a.m43 * b.m31 + a.m44 * b.m41;
            var m42 = a.m41 * b.m12 + a.m42 * b.m22 + a.m43 * b.m32 + a.m44 * b.m42;
            var m43 = a.m41 * b.m13 + a.m42 * b.m23 + a.m43 * b.m33 + a.m44 * b.m43;
            var m44 = a.m41 * b.m14 + a.m42 * b.m24 + a.m43 * b.m34 + a.m44 * b.m44;

            ret.m11 = m11;
            ret.m12 = m12;
            ret.m13 = m13;
            ret.m14 = m14;
            ret.m21 = m21;
            ret.m22 = m22;
            ret.m23 = m23;
            ret.m24 = m24;
            ret.m31 = m31;
            ret.m32 = m32;
            ret.m33 = m33;
            ret.m34 = m34;
            ret.m41 = m41;
            ret.m42 = m42;
            ret.m43 = m43;
            ret.m44 = m44;

            return ret;
        }

        public toStringTable(fractionalDigits: number = 3): string {
            return "m11=" + this.m11.toFixed(fractionalDigits)
                + "\tm21=" + this.m21.toFixed(fractionalDigits)
                + "\tm31=" + this.m31.toFixed(fractionalDigits)
                + "\tm41=" + this.m41.toFixed(fractionalDigits)
                + "\nm12=" + this.m12.toFixed(fractionalDigits)
                + "\tm22=" + this.m22.toFixed(fractionalDigits)
                + "\tm32=" + this.m32.toFixed(fractionalDigits)
                + "\tm42=" + this.m42.toFixed(fractionalDigits)
                + "\nm13=" + this.m13.toFixed(fractionalDigits)
                + "\tm23=" + this.m23.toFixed(fractionalDigits)
                + "\tm33=" + this.m33.toFixed(fractionalDigits)
                + "\tm43=" + this.m43.toFixed(fractionalDigits)
                + "\nm14=" + this.m14.toFixed(fractionalDigits)
                + "\tm24=" + this.m24.toFixed(fractionalDigits)
                + "\tm34=" + this.m34.toFixed(fractionalDigits)
                + "\tm44=" + this.m44.toFixed(fractionalDigits);
        }

        private static _POINT: Point3D = new Point3D();
        private static _MATRIX3D = new Matrix3D();
    }
}

namespace jsidea.geom {
    /**
    *  
    * @author Jran Benker
    * 
    */
    export class MatrixFit {
        public static quad(from: IQuadValue, to: IQuadValue): Matrix2D {
            var s = MatrixFit.basis(from);
            var d = MatrixFit.basis(to);
            var ad = Matrix2D.adjugate(s);
            return d.append(ad).normalize();
        }

        public static fromQuad(origin: IPoint2DValue, width: number, height: number, to: IQuadValue): Matrix2D {
            var from = new geom.Quad();
            from.setRect(-origin.x, -origin.y, width, height);
            var m = MatrixFit.quad(from, to);
            m.prependPositionRaw(-origin.x, -origin.y);
            return m;
        }

        private static basis(quad: IQuadValue): Matrix2D {
            var m = new Matrix2D();
            m.m11 = quad.p1.x;
            m.m12 = quad.p1.y;
            m.m13 = 1;
            m.m21 = quad.p2.x;
            m.m22 = quad.p2.y;
            m.m23 = 1;
            m.m31 = quad.p3.x;
            m.m32 = quad.p3.y;
            m.m33 = 1;
            var adj = Matrix2D.adjugate(m);
            var v = adj.transform(quad.p4);
            var ma = new Matrix2D();
            ma.m11 = v.x;
            ma.m22 = v.y;
            ma.m33 = v.w;
            return m.append(ma);
        }
        
        /**
         * @param from Vector of 2D points in source coordinate system
         * @param to Vector of 2D points in target coordinate system
         * @return A 2D matrix which transform from source to target.
         */
        public static affine2D(from: IPoint2DValue[], to: IPoint2DValue[]): Matrix2D {
            if (from.length != to.length || from.length < 1)
                throw new Error("Size missmatch: 'from' and 'to' must be of same size.");

            var from_pt: number[][] = []
            var to_pt: number[][] = [];
            var l: number = from.length;
            for (var i: number = 0; i < l; ++i) {
                from_pt.push([from[i].x, from[i].y]);
                to_pt.push([to[i].x, to[i].y]);
            }

            var sol: number[][] = this.solve(from_pt, to_pt);
            var matrix = new geom.Matrix2D();
            return matrix.setData(
                [//column_0
                    sol[0][0],
                    sol[0][1],
                
                    //column_1
                    sol[1][0],
                    sol[1][1],
                
                    //column_2
                    sol[2][0],
                    sol[2][1]]
            );
        }
        
        /**
         * @param from Vector of 3D points in source coordinate system
         * @param to Vector of 3D points in target coordinate system
         * @return A 3D matrix which transform from source to target.
         */
        public static affine3D(from: IPoint3DValue[], to: IPoint3DValue[]): Matrix3D {
            if (from.length != to.length || from.length < 1)
                throw new Error("Size missmatch: 'from' and 'to' must be of same size.");

            var from_pt: number[][] = [];
            var to_pt: number[][] = [];
            var l: number = from.length;
            for (var i: number = 0; i < l; ++i) {
                from_pt.push([from[i].x, from[i].y, from[i].z]);
                to_pt.push([to[i].x, to[i].y, to[i].z]);
            }

            var sol: number[][] = this.solve(from_pt, to_pt);
            var matrix = new geom.Matrix3D();
            return matrix.setData(
                [
                    //column_0
                    sol[0][0],
                    sol[0][1],
                    sol[0][2],
                    0,
                    
                    //column_1
                    sol[1][0],
                    sol[1][1],
                    sol[1][2],
                    0,
                    
                    //column_2
                    sol[2][0],
                    sol[2][1],
                    sol[2][2],
                    0,
                    
                    //column_3
                    sol[3][0],
                    sol[3][1],
                    sol[3][2],
                    1
                ]);
        }
        
        /**
         * Based on "Fit an affine transformation to given points", by Jarno Elonen (2007)
         * http://elonen.iki.fi/code/misc-notes/affine-fit/.
         * That code based on the paper "Fitting affine and orthogonal transformations
         * between two sets of points" by Helmuth Spth (2003).
         * 
         * Its ordered by columns. To access an entry use this format 'matrix[column_index][row_index]'.
         * @param from Vector of N-dimensional points in source coordinate system
         * @param to Vector of N-dimensional points in target coordinate system
         * @return A reduced (N+1)x(N) matrix which transform N-dimensional vectors from source to target.
         */
        private static solve(from: number[][], to: number[][]): number[][] {
            //check pre-conditions
            if (from.length != to.length || from.length < 1)
                throw new Error("Size missmatch: 'from' and 'to' must be of same size.");
            if (from[0].length != to[0].length)
                throw new Error("Dimension missmatch: 'from' has the dimension " + from[0].length + " and 'to' " + to[0].length + ".");
            if (from.length < from[0].length + 1)
                throw new Error("Too few points: under-determined system, you need " + (from[0].length + 1 - from.length) + " more reference points for a 'overconstrained' system.");
            
            //the dimensions to solve (point: 2, vector3D: 3, ...)
            var dim: number = from[0].length;
            
            //re-usable variables
            var i: number;
            var j: number;
            var k: number;
            var e: number[];
            
            //create "acc": empty (N)x(N+1) matrix
            var acc: number[][] = [];
            for (i = 0; i < dim + 1; ++i) {
                acc[i] = [];
                for (j = 0; j < dim; ++j)
                    acc[i][j] = 0;
            }
            
            //fill "acc" matrix
            for (i = 0; i < dim + 1; ++i)
                for (j = 0; j < dim; ++j)
                    for (k = 0; k < from.length; ++k) {
                        e = from[k].slice();
                        e.push(1);
                        acc[i][j] += e[i] * to[k][j];
                    }   
            
            //create "Q": empty (N+1)x(N+1) matrix
            var Q: number[][] = [];
            for (i = 0; i < dim + 1; ++i) {
                Q[i] = [];
                for (j = 0; j < dim + 1; ++j)
                    Q[i][j] = 0;
            }
            
            //fill "Q" matrix
            for (k = 0; k < from.length; ++k) {
                e = from[k].slice();
                e.push(1);
                for (i = 0; i < dim + 1; ++i)
                    for (j = 0; j < dim + 1; ++j)
                        Q[i][j] += e[i] * e[j];
            }
            
            //concat "Q" with "acc" by each entry pair
            var matrix: number[][] = [];
            for (i = 0; i < dim + 1; ++i) {
                matrix[i] = Q[i].slice();
                for (j = 0; j < dim; ++j)
                    matrix[i].push(acc[i][j]);
            }
            
            //solve Q * a' = acc with by Gauss-Jordan
            var solved: Boolean = this.gaussJordan(matrix);
            if (!solved)
                throw new Error("Could not solve affine transformation. Singular matrix: points are probably coplanar.");
            
            //remove unnecessary entries
            var l: number = matrix.length;
            var off: number = matrix[0].length - dim;
            for (i = 0; i < l; ++i)
                matrix[i] = matrix[i].slice(off);

            return matrix;
        }
        
        /**
         * Transforms a N-dimensional vector to a N-dimensional vector using the given matrix.
         * @param matrix A (N+1)x(N) matrix to tranform a vector.
         * @param vector A N-dimensional vector. 
         * @param result A pointer for the result to write in.
         * @return The transformed vector.         * 
         */
        private static transform(
            matrix: number[][],
            vector: number[],
            result: number[] = null): number[] {
            var dim: number = vector.length;
            result = result || [];
            for (var i: number = 0; i < dim; ++i)
                result[i] = 0;
            for (i = 0; i < dim; ++i) {
                for (var j: number = 0; j < dim; ++j)
                    result[i] += vector[j] * matrix[j][i];
                result[i] += matrix[dim][i]
            }
            return result;
        }

        private static gaussJordan(matrix: number[][]): Boolean {
            //re-usable index variables
            var i: number;
            var j: number;
            var k: number

            var h: number = matrix.length;
            var w: number = matrix[0].length;
            for (i = 0; i < h - 1; ++i) {
                //find max pivot
                var maxrow: number = i;
                for (j = i + 1; j < h; ++j)
                    if (Math.abs(matrix[j][i]) > Math.abs(matrix[maxrow][i]))
                        maxrow = j;
                
                //swap...?
                var tmp: number[] = matrix[maxrow];
                matrix[maxrow] = matrix[i];
                matrix[i] = tmp;
                
                //if singular return false
                if (Math.abs(matrix[i][i]) <= 1e-10)
                    return false;
                
                //eliminate column y
                for (j = i + 1; j < h; ++j) {
                    var c: number = matrix[j][i] / matrix[i][i];
                    for (k = i; k < w; ++k)
                        matrix[j][k] -= matrix[i][k] * c;
                }
            }
            
            //backsubstitute
            for (i = h - 1; i >= 0; --i) {
                c = matrix[i][i];
                for (j = 0; j < i; ++j)
                    for (k = w - 1; k >= i; --k)
                        matrix[j][k] -= matrix[i][k] * matrix[j][i] / c;
                matrix[i][i] /= c;
                //normalize row y
                for (k = h; k < w; ++k)
                    matrix[i][k] /= c;
            }

            return true;
        }
    }
}

namespace jsidea.geom {
    /**
    *  
    * @author Jran Benker
    * 
    */
    export class MatrixUtil {
        private static tempAxeX: Point3D = new Point3D();
        private static tempAxeY: Point3D = new Point3D();
        private static tempAxeZ: Point3D = new Point3D();

        public static lookAt(eye: Point3D, target: Point3D, up: Point3D): Matrix3D {
            var x: Point3D = MatrixUtil.tempAxeX;
            var y: Point3D = MatrixUtil.tempAxeY;
            var z: Point3D = MatrixUtil.tempAxeZ;

            z.difference(eye, target).normalize();
            if (z.length() === 0) {
                z.z = 1;
            }
            x.crossSet(up, z).normalize();
            if (x.length() === 0) {
                z.x += 0.0001;
                x.crossSet(up, z).normalize();
            }
            y.crossSet(z, x);

            var m = new Matrix3D();
            m.m11 = x.x;
            m.m12 = x.y;
            m.m13 = x.z;
            m.m21 = y.x;
            m.m22 = y.y;
            m.m23 = y.z;
            m.m31 = z.x;
            m.m32 = z.y;
            m.m33 = z.z;
            return m;
        }
    }
}

namespace jsidea.geom {
    export class Point2D implements IPoint2DValue {

        public static X_AXIS: Point2D = new Point2D(1, 0);
        public static Y_AXIS: Point2D = new Point2D(0, 1);

        constructor(
            public x: number = 0,
            public y: number = 0,
            public w: number = 1) {
        }

        public static create(
            x: number = 0,
            y: number = 0,
            w: number = 1): Point2D {
            return new Point2D(x, y, w);
        }

        public getData(length: number = 2): number[] {
            if (length > 2)
                return [this.x, this.y, this.w];
            return [this.x, this.y];
        }

        public setData(data: number[], offset: number = 0, length: number = 2): Point2D {
            if (data === undefined)
                return;
            this.x = data[0 + offset];
            this.y = data[1 + offset];
            if (length > 2)
                this.w = data[2 + offset];
            return this;
        }
        
        public setTo(x: number, y: number, w: number = 1): Point2D {
            this.x = x;
            this.y = y;
            this.w = w;
            return this;
        }

        public copyFrom(pt: IPoint2DValue): Point2D {
            this.x = pt.x;
            this.y = pt.y;
            this.w = pt.w === undefined ? 1 : pt.w;
            return this;
        }

        public copyTo(target: IPoint2DValue): Point2D {
            target.x = this.x;
            target.y = this.y;
            if (target.w !== undefined)
                target.w = this.w;
            return this;
        }

        public equals(point: IPoint2DValue): boolean {
            return point.x == this.x && point.y == this.y && this.w == (point.w === undefined ? 1 : point.w);
        }

        public length(): number {
            return Math.sqrt(this.x * this.x + this.y * this.y);
        }

        public normalize(length: number = 1): Point2D {
            var len: number = this.length();
            if (!len)
                return;
            this.scalar(length / len);
        }

        public clamp(length: number): Point2D {
            var len: number = this.length();
            if (!len || len <= length)
                return;
            this.scalar(length / len);
        }

        public scalar(scale: number): Point2D {
            this.x *= scale;
            this.y *= scale;
            return this;
        }

        public mul(pt: IPoint2DValue): Point2D {
            this.x *= pt.x;
            this.y *= pt.y;
            return this;
        }

        public product(factorA: IPoint2DValue, factorB: IPoint2DValue): Point2D {
            this.x = factorA.x * factorB.x;
            this.y = factorA.y * factorB.y;
            return this;
        }

        public div(pt: IPoint2DValue): Point2D {
            this.x /= pt.x;
            this.y /= pt.y;
            return this;
        }

        public quotient(divident: IPoint2DValue, divisor: IPoint2DValue): Point2D {
            this.x = divident.x / divisor.x;
            this.y = divident.y / divisor.y;
            return this;
        }

        public dot(pt: IPoint2DValue): number {
            return this.x * pt.x + this.y * pt.y;
        }

        public add(pt: IPoint2DValue): Point2D {
            this.x += pt.x;
            this.y += pt.y;
            return this;
        }

        public translate(x: number, y: number): Point2D {
            this.x += x;
            this.y += y;
            return this;
        }

        public sum(sumA: IPoint2DValue, sumB: IPoint2DValue): Point2D {
            this.x = sumA.x + sumB.x;
            this.y = sumA.y + sumB.y;
            return this;
        }

        public sub(pt: IPoint2DValue): Point2D {
            this.x -= pt.x;
            this.y -= pt.y;
            return this;
        }

        public difference(minuend: IPoint2DValue, subtrahend: IPoint2DValue): Point2D {
            this.x = minuend.x - subtrahend.x;
            this.y = minuend.y - subtrahend.y;
            return this;
        }

        public clone(): Point2D {
            return new Point2D(this.x, this.y);
        }

        public cross(pt: IPoint2DValue): Point2D {
            var x = this.x;
            var y = this.y;
            this.x = y * pt.x - x * pt.y;
            this.y = x * pt.y - y * pt.x;
            return this;
        }

        public static interpolate(v0: IPoint2DValue, v1: IPoint2DValue, f: number, ret: Point2D = new Point2D()): Point2D {
            return ret.setTo(
                v0.x + (v1.x - v0.x) * f,
                v0.y + (v1.y - v0.y) * f);
        }

        public static polar(angle: number, length: number, ret: Point2D = new Point2D()): Point2D {
            var angRad = angle * math.Number.DEG_TO_RAD;
            return ret.setTo(Math.cos(angRad) * length, Math.sin(angRad) * length);
        }

        public static distance(v0: IPoint2DValue, v1: IPoint2DValue): number {
            var dx: number = v0.x - v1.x;
            var dy: number = v0.y - v1.y;
            return Math.sqrt(dx * dx + dy * dy);
        }

        public static reflect(direction: IPoint2DValue, normal: IPoint2DValue, ret: Point2D = new Point2D()): Point2D {
            var dp: number = direction.x * normal.x + direction.y * normal.y;
            return ret.setTo(direction.x - 2 * dp * normal.x, direction.y - 2 * dp * normal.y);
        }

        public static intersection(p1: IPoint2DValue, p2: IPoint2DValue, p3: IPoint2DValue, p4: IPoint2DValue, ret: Point2D = new Point2D()): Point2D {
            var x1: number = p1.x;
            var y1: number = p1.y;
            var x4: number = p4.x;
            var y4: number = p4.y;

            var dx1: number = p2.x - x1;
            var dx2: number = p3.x - x4;

            if (!dx1 && !dx2)
                return null;

            var m1: number = (p2.y - y1) / dx1;
            var m2: number = (p3.y - y4) / dx2;

            if (!dx1)
                return ret.setTo(x1, m2 * (x1 - x4) + y4);// infinity
            else if (!dx2)
                return ret.setTo(x4, m1 * (x4 - x1) + y1);// infinity
            
            var xInt: number = (-m2 * x4 + y4 + m1 * x1 - y1) / (m1 - m2);
            var yInt: number = m1 * (xInt - x1) + y1;
            if (isNaN(xInt) || isNaN(yInt))
                return null;
            return ret.setTo(xInt, yInt);
        }
    }
}

namespace jsidea.geom {
    export class Point3D implements IPoint3DValue {

        public static X_AXIS: Point3D = new Point3D(1, 0, 0);
        public static Y_AXIS: Point3D = new Point3D(0, 1, 0);
        public static Z_AXIS: Point3D = new Point3D(0, 0, 1);

        constructor(
            public x: number = 0,
            public y: number = 0,
            public z: number = 0,
            public w: number = 1) {
        }

        public static create(
            x: number = 0,
            y: number = 0,
            z: number = 0,
            w: number = 1): Point3D {
            return new Point3D(x, y, z, w);
        }

        public getData(): number[] {
            return [this.x, this.y, this.z];
        }

        public setData(data: number[], offset: number = 0): Point3D {
            if (data === undefined)
                return;
            this.x = data[0 + offset];
            this.y = data[1 + offset];
            this.z = data[2 + offset];
            this.w = data.length > (3 + offset) ? data[3 + offset] : 1;
        }

        public copyFrom(pt: IPoint3DValue): Point3D {
            this.x = pt.x;
            this.y = pt.y;
            this.z = pt.z;
            this.w = pt.w;
            return this;
        }

        public copyTo(target: IPoint3DValue): Point3D {
            target.x = this.x;
            target.y = this.y;
            target.z = this.z;
            target.w = this.w;
            return this;
        }

        public equals(point: IPoint3DValue): boolean {
            return point.x == this.x && point.y == this.y && point.z == this.z;
        }

        public length(): number {
            return Math.sqrt(this.x * this.x + this.y * this.y + this.z * this.z);
        }

        public normalize(length: number = 1): Point3D {
            var len: number = this.length();
            if (!len)
                return;
            this.scalar(length / len);
        }

        public clamp(length: number): Point3D {
            var len: number = this.length();
            if (!len || len <= length)
                return;
            this.scalar(length / len);
        }

        public scalar(scale: number): Point3D {
            this.x *= scale;
            this.y *= scale;
            this.z *= scale;
            return this;
        }

        public setTo(x: number, y: number, z: number, w: number = 1): Point3D {
            this.x = x;
            this.y = y;
            this.z = z;
            this.w = w;
            return this;
        }

        public cross(pt: IPoint3DValue): Point3D {
            var x = this.x;
            var y = this.y;
            var z = this.z;
            this.x = y * pt.z - z * pt.y;
            this.y = z * pt.x - x * pt.z;
            this.z = x * pt.y - y * pt.x;
            return this;
        }

        public crossSet(a: IPoint3DValue, b: IPoint3DValue): Point3D {
            var ax = a.x;
            var ay = a.y;
            var az = a.z;
            var bx = b.x;
            var by = b.y;
            var bz = b.z;
            this.x = ay * bz - az * by;
            this.y = az * bx - ax * bz;
            this.z = ax * by - ay * bx;
            return this;
        }

        public mul(pt: IPoint3DValue): Point3D {
            this.x *= pt.x;
            this.y *= pt.y;
            this.z *= pt.z;
            return this;
        }

        public product(factorA: IPoint3DValue, factorB: IPoint3DValue): Point3D {
            this.x = factorA.x * factorB.x;
            this.y = factorA.y * factorB.y;
            this.z = factorA.z * factorB.z;
            return this;
        }

        public div(divisor: IPoint3DValue): Point3D {
            this.x /= divisor.x;
            this.y /= divisor.y;
            this.z /= divisor.z;
            return this;
        }

        public quotient(divident: IPoint3DValue, divisor: IPoint3DValue): Point3D {
            this.x = divident.x / divisor.x;
            this.y = divident.y / divisor.y;
            this.z = divident.z / divisor.z;
            return this;
        }

        public dot(pt: IPoint3DValue): number {
            return this.x * pt.x + this.y * pt.y + this.z * pt.z;
        }

        public add(summand: IPoint3DValue): Point3D {
            this.x += summand.x;
            this.y += summand.y;
            this.z += summand.z;
            return this;
        }
        
        public translate(x: number, y: number, z: number): Point3D {
            this.x += x;
            this.y += y;
            this.z += z;
            return this;
        }

        public sum(summandA: IPoint3DValue, summandB: IPoint3DValue): Point3D {
            this.x = summandA.x + summandB.x;
            this.y = summandA.y + summandB.y;
            this.z = summandA.z + summandB.z;
            return this;
        }

        public sub(subtrahend: IPoint3DValue): Point3D {
            this.x -= subtrahend.x;
            this.y -= subtrahend.y;
            this.z -= subtrahend.z;
            return this;
        }

        public difference(minuend: IPoint3DValue, subtrahend: IPoint3DValue): Point3D {
            this.x = minuend.x - subtrahend.x;
            this.y = minuend.y - subtrahend.y;
            this.z = minuend.z - subtrahend.z;
            return this;
        }

        public clone(): Point3D {
            return new Point3D(this.x, this.y, this.z, this.w);
        }

        public static interpolate(v0: IPoint3DValue, v1: IPoint3DValue, f: number): Point3D {
            return new Point3D(
                v0.x + (v1.x - v0.x) * f,
                v0.y + (v1.y - v0.y) * f,
                v0.z + (v1.z - v0.z) * f);
        }

        //TODO: implement it
        public static polar(angle: IPoint3DValue, length: number, ret: Point3D = new Point3D()): Point3D {
            var angRad = angle.x * math.Number.DEG_TO_RAD;
            ret.x = Math.cos(angRad) * length;
            ret.y = Math.sin(angRad) * length;
            return ret;
        }

        public static distance(v0: IPoint3DValue, v1: IPoint3DValue): number {
            var dx: number = v0.x - v1.x;
            var dy: number = v0.y - v1.y;
            var dz: number = v0.z - v1.z;
            return Math.sqrt(dx * dx + dy * dy + dz * dz);
        }

        public static reflect(vector: IPoint3DValue, normal: IPoint3DValue): Point3D {
            var dp: number = vector.x * normal.x + vector.y * normal.y + vector.z * normal.z;
            return new Point3D(vector.x - 2 * dp * normal.x, vector.y - 2 * dp * normal.y, vector.z - 2 * dp * normal.z);
        }
    }
}

namespace jsidea.geom {
    export class Quad implements IQuadValue {
        public points: geom.Point3D[] = [];
        constructor(
            public p1: geom.Point3D = new geom.Point3D(),
            public p2: geom.Point3D = new geom.Point3D(),
            public p3: geom.Point3D = new geom.Point3D(),
            public p4: geom.Point3D = new geom.Point3D()
        ) {
            this.points = [this.p1, this.p2, this.p3, this.p4];
        }

        public clone(): Quad {
            return new Quad(
                this.p1.clone(),
                this.p2.clone(),
                this.p3.clone(),
                this.p4.clone());
        }

        public setRect(x: number, y: number, width: number, height: number): Quad {
            this.p1.x = x;
            this.p1.y = y;
            this.p2.x = x + width;
            this.p2.y = y;
            this.p3.x = x;
            this.p3.y = y + height;
            this.p4.x = x + width;
            this.p4.y = y + height;
            return this;
        }

        public setData(
            data: IPoint3DValue[]): Quad {
            this.p1.copyFrom(data[0]);
            this.p2.copyFrom(data[1]);
            this.p3.copyFrom(data[2]);
            this.p4.copyFrom(data[3]);
            return this;
        }

        public setTo(
            p1: geom.IPoint3DValue,
            p2: geom.IPoint3DValue,
            p3: geom.IPoint3DValue,
            p4: geom.IPoint3DValue): Quad {
            this.p1.copyFrom(p1);
            this.p2.copyFrom(p2);
            this.p3.copyFrom(p3);
            this.p4.copyFrom(p4);
            return this;
        }

        public copyFrom(value: IQuadValue): Quad {
            return this.setTo(value.p1, value.p2, value.p3, value.p4);
        }

        public dispose(): void {
        }
    }
}

namespace jsidea.geom {
    export class Rect2D implements IRect2DValue {
        constructor(
            public x: number = 0,
            public y: number = 0,
            public width: number = 0,
            public height: number = 0) {
        }

        public clone(): Rect2D {
            return new Rect2D(
                this.x,
                this.y,
                this.width,
                this.height);
        }

        public setTo(x: number, y: number, width: number, height: number): Rect2D {
            this.x = x;
            this.y = y;
            this.width = width;
            this.height = height;

            return this;
        }

        public toQuad(ret: Quad = new Quad()): Quad {
            ret.p1.x = this.x;
            ret.p1.y = this.y;
            ret.p2.x = this.right;
            ret.p2.y = this.y;
            ret.p3.x = this.right;
            ret.p3.y = this.bottom;
            ret.p4.x = this.x;
            ret.p4.y = this.bottom;
            return ret;
        }

        public setCSS(clipCSS: string): Rect2D {
            if (!clipCSS || clipCSS == "auto")
                return this;
            //TODO: using a regex -> performance testing
            var str = clipCSS.toLowerCase().replace("rect(", "").replace(")", "");
            var values = system.Engine.isWebKit ? str.split(" ") : str.split(",");

            this.x = math.Number.parse(values[3], 0);
            this.y = math.Number.parse(values[0], 0);
            this.width = math.Number.parse(values[1], 0) - this.x;
            this.height = math.Number.parse(values[2], 0) - this.y;

            return this;
        }

        public getCSS(): string {
            return "rect("
                + Math.round(this.y) + "px, "
                + Math.round(this.right) + "px, "
                + Math.round(this.bottom) + "px, "
                + Math.round(this.x) + "px)";
        }

        public copyFrom(value: IRect2DValue): void {
            this.x = value.x;
            this.y = value.y;
            this.width = value.width;
            this.height = value.height;
        }

        public center(ret: geom.Point2D = new geom.Point2D()): geom.Point2D {
            return ret.setTo(this.x + this.width * 0.5, this.y + this.width * 0.5);
        }

        public equals(value: IRect2DValue, difference: number = 0): boolean {
            return Math.abs(this.x - value.x) <= difference
                && Math.abs(this.y - value.y) <= difference
                && Math.abs(this.width - value.width) <= difference
                && Math.abs(this.height - value.height) <= difference;
        }

        public contains(x: number, y: number): boolean {
            return x >= this.x
                && x <= (this.x + this.width)
                && y >= this.y
                && y <= (this.y + this.height);
        }

        public containsRect(r: IRect2DValue): boolean {
            if (!this.contains(r.x, r.y)
                || !this.contains(r.x + r.width, r.y + r.height)
                || !this.contains(r.x + r.width, r.y)
                || !this.contains(r.x, r.y + r.height)
            )
                return false;
            return true;
        }

        public intersects(r: IRect2DValue): boolean {
            if (this.contains(r.x, r.y)
                || this.contains(r.x + r.width, r.y + r.height)
                || this.contains(r.x + r.width, r.y)
                || this.contains(r.x, r.y + r.height)
            )
                return true;
            return false;
        }

        public copyFromClientRect(rect: ClientRect): Rect2D {
            this.x = rect.left;
            this.y = rect.top;
            this.width = rect.width;
            this.height = rect.height;
            return this;
        }

        public get right(): number {
            return this.x + this.width;
        }

        public set right(value: number) {
            this.width = value - this.x;
        }

        public get bottom(): number {
            return this.y + this.height;
        }

        public set bottom(value: number) {
            this.height = value - this.y;
        }

        public static getBounds(element: HTMLElement, ret: Rect2D = new Rect2D()): Rect2D {
            ret.copyFromClientRect(element.getBoundingClientRect());
            if (system.Engine.isWebKit) {
                //                var style = layout.Style.create(element);
                //                if (style.transformStyle == "preserve-3d" && style.transform.indexOf("matrix3d") >= 0) {
                //                    var mat = geom.Matrix3D.create(element, style);
                //                    var bnd = mat.bounds(0, 0, element.offsetWidth, element.offsetHeight);
                //                    var pt = mat.transformRaw(element.offsetWidth, element.offsetHeight, 0);
                ////                    console.log(element.clientLeft, element.offsetWidth, bnd.toString());
                ////                    console.log(element.offsetWidth - bnd.width);
                ////                    console.log(element.offsetHeight - bnd.height);
                //                    console.log(bnd.width, bnd.height, ret.width, ret.height);
                //                    ret.x -= bnd.width - ret.width;
                //                    ret.y -= bnd.height - ret.height;
                ////                    ret.width = bnd.width;
                ////                    ret.height = bnd.height;
                //                }

                ret.x += document.body.scrollLeft;
                ret.y += document.body.scrollTop;
            }
            else {
                ret.x += document.documentElement.scrollLeft;
                ret.y += document.documentElement.scrollTop;
            }
            return ret;
        }

        public static getClip(element: HTMLElement, style: CSSStyleDeclaration, ret: geom.Rect2D = new geom.Rect2D()): geom.Rect2D {
            if (!style.clip || style.clip == "auto") {
                ret.x = 0;
                ret.y = 0;
                ret.width = element.offsetWidth;
                ret.height = element.offsetHeight;
                return ret;
            }
            ret.setCSS(style.clip);
            return ret;
        }

        public dispose(): void {
        }

        public toString(): string {
            return "[ jsidea.geom.Box2D"
                + " x=" + this.x
                + " y=" + this.y
                + " width=" + this.width
                + " height=" + this.height
                + "]";
        }
    }
}

namespace jsidea.layout {
    export class Box {

        public element: HTMLElement = null;
        public style: CSSStyleDeclaration;

        public offsetWidth: number = 0;
        public offsetHeight: number = 0;
        public parentWidth: number = 0;
        public parentHeight: number = 0;

        public marginTop: number = 0;
        public marginRight: number = 0;
        public marginBottom: number = 0;
        public marginLeft: number = 0;

        public borderTop: number = 0;
        public borderRight: number = 0;
        public borderBottom: number = 0;
        public borderLeft: number = 0;

        public paddingTop: number = 0;
        public paddingRight: number = 0;
        public paddingBottom: number = 0;
        public paddingLeft: number = 0;

        public scrollLeft: number = 0;
        public scrollTop: number = 0;

        constructor(element?: HTMLElement, style?: CSSStyleDeclaration) {
            if (element)
                this.update(element, style);
        }

        public static create(element?: HTMLElement, style?: CSSStyleDeclaration): Box {
            return new Box(element, style);
        }

        public update(element: HTMLElement, style?: CSSStyleDeclaration): Box {
            if (!element)
                return this.clear();
            style = style || window.getComputedStyle(element);

            this.style = style;
            this.element = element;

            this.offsetWidth = element.offsetWidth;
            this.offsetHeight = element.offsetHeight;
            this.scrollLeft = element.scrollLeft;
            this.scrollTop = element.scrollTop;

            if (system.Engine.isWebKit) {
                if (element == element.ownerDocument.body) {
                    this.scrollLeft = 0;
                    this.scrollTop = 0;
                }
                else if (system.Engine.isWebKit && element == element.ownerDocument.documentElement) {
                    this.scrollLeft = element.ownerDocument.body.scrollLeft;
                    this.scrollTop = element.ownerDocument.body.scrollTop;
                }
            }

            if (element.parentElement) {
                this.parentWidth = element.parentElement.clientWidth;
                this.parentHeight = element.parentElement.clientHeight;
            }
            else {
                this.parentWidth = window.innerWidth;
                this.parentHeight = window.innerHeight;
            }

            var w = this.parentWidth;
            var h = this.parentHeight;
            this.marginTop = math.Number.relation(style.marginTop, h, 0);
            this.marginRight = math.Number.relation(style.marginRight, w, 0);
            this.marginBottom = math.Number.relation(style.marginBottom, h, 0);
            this.marginLeft = math.Number.relation(style.marginLeft, w, 0);

            this.paddingTop = math.Number.relation(style.paddingTop, h, 0);
            this.paddingRight = math.Number.relation(style.paddingRight, w, 0);
            this.paddingBottom = math.Number.relation(style.paddingBottom, h, 0);
            this.paddingLeft = math.Number.relation(style.paddingLeft, w, 0);

            this.borderTop = math.Number.parse(style.borderTopWidth, 0);
            this.borderRight = math.Number.parse(style.borderRightWidth, 0);
            this.borderBottom = math.Number.parse(style.borderBottomWidth, 0);
            this.borderLeft = math.Number.parse(style.borderLeftWidth, 0);

            return this;
        }

        public copyFrom(size: Box): Box {
            this.element = size.element;
            this.style = size.style;
            this.offsetWidth = size.offsetWidth;
            this.offsetHeight = size.offsetHeight;
            this.parentWidth = size.parentWidth;
            this.parentHeight = size.parentHeight;

            this.marginTop = size.marginTop;
            this.marginRight = size.marginRight;
            this.marginBottom = size.marginBottom;
            this.marginLeft = size.marginLeft;

            this.paddingTop = size.paddingTop;
            this.paddingRight = size.paddingRight;
            this.paddingBottom = size.paddingBottom;
            this.paddingLeft = size.paddingLeft;

            this.borderTop = size.borderTop;
            this.borderRight = size.borderRight;
            this.borderBottom = size.borderBottom;
            this.borderLeft = size.borderLeft;

            return this;
        }

        public clone(): Box {
            return (new Box()).copyFrom(this);
        }

        public clear(): Box {
            this.element = null;
            this.style = null;

            this.scrollLeft = 0;
            this.scrollTop = 0;

            this.offsetWidth = 0;
            this.offsetHeight = 0;
            this.parentWidth = 0;
            this.parentHeight = 0;

            this.marginTop = 0;
            this.marginRight = 0;
            this.marginBottom = 0;
            this.marginLeft = 0;

            this.paddingTop = 0;
            this.paddingRight = 0;
            this.paddingBottom = 0;
            this.paddingLeft = 0;

            this.borderTop = 0;
            this.borderRight = 0;
            this.borderBottom = 0;
            this.borderLeft = 0;

            return this;
        }

        public apply(element: HTMLElement): Box {
            var style = element.style;
            //TODO:
            //keep units here (percentage should keep percentage)
            style.marginTop = this.marginTop + "px";
            style.marginRight = this.marginRight + "px";
            style.marginBottom = this.marginBottom + "px";
            style.marginLeft = this.marginLeft + "px";

            style.paddingTop = this.paddingTop + "px";
            style.paddingRight = this.paddingRight + "px";
            style.paddingBottom = this.paddingBottom + "px";
            style.paddingLeft = this.paddingLeft + "px";

            style.borderTop = this.borderTop + "px";
            style.borderRight = this.borderRight + "px";
            style.borderBottom = this.borderBottom + "px";
            style.borderLeft = this.borderLeft + "px";

            return this;
        }

        /**
        * Calculates the bounds of the box-model relative to the toBox-model.
        * @param boxModel The size/bounds-boxModel.
        * @param toBox Where the bounds should be relative to.
        * @return The bounds of the box-model relative to the toBox.
        */
        public bounds(boxModel?: IBoxModel, toBox?: IBoxModel, ret: geom.Rect2D = new geom.Rect2D()): geom.Rect2D {
            boxModel = boxModel || BoxModel.BORDER;
            toBox = toBox || BoxModel.BORDER;

            var pt = new geom.Point3D(0, 0);
            this.transform(pt, boxModel, toBox);

            ret.x = pt.x;
            ret.y = pt.y;
            ret.width = boxModel.width(this);
            ret.height = boxModel.height(this);

            return ret;
        }
        
        /**
        * Transforms a point from one box-model to another. It does not clone the point, it will change it.
        * @param point The point to transform. The coordinate system should be the fromBox-model.
        * @param fromBox Where the point coordinates coming from.
        * @param toBox The target coordinate-system/box-model.
        * @return The transformed point.
        */
        public transform(point: geom.Point3D, fromBox?: IBoxModel, toBox?: IBoxModel): geom.Point3D {
            if (toBox == fromBox)
                return point;
            if (fromBox)
                fromBox.toBorderBox(this, point);
            if (toBox)
                toBox.fromBorderBox(this, point);
            return point;
        }

        public width(boxModel?: IBoxModel): number {
            return (boxModel || BoxModel.BORDER).width(this);
        }

        public height(boxModel?: IBoxModel): number {
            return (boxModel || BoxModel.BORDER).height(this);
        }

        public dispose(): void {
            this.clear();
        }

        public static lookup(boxSizing: string): IBoxModel {
            for (var model in BoxModel) {
                if ((<IBoxModel>model).name == boxSizing)//v != this && 
                    return model;
            }
            return null;
        }
    }
}

namespace jsidea.layout.BoxModel {
    class Attachment extends Background {
        public name: string = "attachment-box";
        protected getBackgroundBox(size: Box, ret: geom.Rect2D = new geom.Rect2D()): geom.Rect2D {
            super.getBackgroundBox(size, ret);
            //TODO: implement attachment handling corpointly
            var attachment = size.style.backgroundAttachment;
            if (attachment == "scroll")
            { }
            else if (attachment == "local") {
                ret.x -= size.scrollLeft;
                ret.y -= size.scrollTop;
            }
            else if (attachment == "fixed") {
                if (system.Engine.isWebKit) {
                    ret.x += size.element.ownerDocument.body.scrollLeft;
                    ret.y += size.element.ownerDocument.body.scrollTop;
                }
                else {
                    ret.x += size.element.ownerDocument.documentElement.scrollLeft;
                    ret.y += size.element.ownerDocument.documentElement.scrollTop;
                }
                //transform to html
                var trans = Transform.create(size.element);
                var gl = trans.localToGlobal(0, 0, 0, BoxModel.PADDING, BoxModel.BORDER);
                ret.x -= gl.x;
                ret.y -= gl.y;
            }
            return ret;
        }
        public fromBorderBox(size: Box, point: geom.Point3D): void {
            var bb = this.getBackgroundBox(size);
            var scaleX = bb.width / this._imageWidth;
            var scaleY = bb.height / this._imageHeight;
            point.x -= bb.x;
            point.y -= bb.y;
            point.x /= scaleX;
            point.y /= scaleY;
        }
        public toBorderBox(size: Box, point: geom.Point3D): void {
            var bb = this.getBackgroundBox(size);
            var scaleX = bb.width / this._imageWidth;
            var scaleY = bb.height / this._imageHeight;
            point.x *= scaleX;
            point.y *= scaleY;
            point.x += bb.x;
            point.y += bb.y;
        }
    }
    export var ATTACHMENT: IBoxModel = new Attachment();
}

namespace jsidea.layout.BoxModel {
    export class Background implements IBoxModel {
        public name: string = "background-box";
        private _image = new Image();
        private _point = new geom.Point3D();
        protected _imageWidth = 0;
        protected _imageHeight = 0;
        protected getBackgroundBox(size: Box, ret: geom.Rect2D = new geom.Rect2D()): geom.Rect2D {
            var src = size.style.backgroundImage.replace(/url\((['"])?(.*?)\1\)/gi, '$2').split(',')[0];
            var width: number;
            var height: number;
            if (!src || src == "none") {
                width = size.offsetWidth;
                height = size.offsetHeight;
            }
            else {
                this._image.src = src;
                width = this._image.width;
                height = this._image.height;
                if (isNaN(width))
                    width = size.offsetWidth;
                if (isNaN(height))
                    height = size.offsetHeight;
            }
            //TODO: throw error or something similar
            if (!width || !height)
                return ret.setTo(0, 0, size.offsetWidth, size.offsetHeight);

            this._imageWidth = width;
            this._imageHeight = height;

            var backgroundOrigin = size.style.backgroundOrigin ? Box.lookup(size.style.backgroundOrigin) : BoxModel.PADDING;
            var origin = size.bounds(backgroundOrigin);

            var x = 0;
            var y = 0;

            var cssPos = size.style.backgroundPosition.split(" ");
            var xPos: string = cssPos[0] || "auto";
            var yPos: string = cssPos[1] || "auto";
            var cssSize = size.style.backgroundSize.split(" ");
            var xSize: string = cssSize[0] || "auto";
            var ySize: string = cssSize[1] || xSize;
            var scaleX = origin.width / width;
            var scaleY = origin.height / height;

            if (xSize == "auto")
            { }
            else if (xSize == "cover")
                width *= Math.max(scaleX, scaleY);
            else if (xSize == "contain")
                width *= Math.min(scaleX, scaleY);
            else
                width = math.Number.relation(xSize, origin.width, origin.width);

            if (ySize == "auto") {
                //keep ratio
                if (xSize != "auto")
                    height *= width / this._imageWidth;
            }
            else if (ySize == "cover")
                height *= Math.max(scaleX, scaleY);
            else if (ySize == "contain")
                height *= Math.min(scaleX, scaleY);
            else
                height = math.Number.relation(ySize, origin.height, origin.height);

            //keep ratio
            if (xSize == "auto" && ySize != "auto")
                width *= height / this._imageHeight;

            if (xPos == "auto")
            { }
            else if (xPos.indexOf("%") > 0)
                x = math.Number.relation(xPos, origin.width, 0) - math.Number.relation(xPos, width, 0);
            else
                x = math.Number.relation(xPos, origin.width, 0);

            if (yPos == "auto")
            { }
            else if (yPos.indexOf("%") > 0)
                y = math.Number.relation(yPos, origin.height, 0) - math.Number.relation(yPos, height, 0);
            else
                y = math.Number.relation(yPos, origin.height, 0);

            //back to border box
            var pt = size.transform(this._point.setTo(x, y, 0), backgroundOrigin, BoxModel.BORDER);

            return ret.setTo(pt.x, pt.y, width, height);
        }
