        private getURL(sym: ISymbol): string {
            var base = "http://127.0.0.1/eventfive/jsidea-build/bin/";
            if (sym.kind == 213)
                base += "interfaces/";
            else if (sym.kind == 212)
                base += "classes/";
            else if (sym.kind == 209)
                base += "modules/";
            base += sym.module.fullName.toLowerCase();
            if (sym.kind == 209)//variable declaration
                base += ".html#" + sym.name.toLowerCase();
            else if (sym.kind == 212 || sym.kind == 213)//class or interface
                base += "." + sym.name.toLowerCase() + ".html";
            return base;
        }

        private getOrderedSymbols(): ISymbol[] {
            function addAt(ary: any[], data: any, index: number): any[] {
                var head = ary.slice(0, index);
                var tail = ary.slice(index);
                ary.splice(0, ary.length);
                for (var da of head)
                    ary.push(da);
                ary.push(data);
                for (var da of tail)
                    ary.push(da);
                return ary;
            }

            var symbols = this.symbols;
            var res: ISymbol[] = [];
            for (var ref of symbols) {
                var l = res.length;
                for (var i = 0; i < l; ++i) {
                    //if its used by
                    if (res[i] == ref)
                        continue;
                    if (res[i].relations.indexOf(ref) >= 0) {
                        addAt(res, ref, i);
                        break;
                    }
                }
                if (res.indexOf(ref) === -1)
                    res.push(ref);
            }
            return res;
        }

        public getFileByName(name: string): IFile {
            for (var file of this.files)
                if (file.name == name)
                    return file;
            return null;
        }

        private getByQualifiedName(name: string): ISymbol {
            var refs = this.symbols;
            for (var ref of refs)
                if (ref.fullName == name)
                    return ref;
            return null;
        }

        private createRelations(ref: ISymbol, relations?: ISymbol[]): ISymbol[] {
            relations = relations || [];
            for (var imp of ref.imports) {
                if (relations.indexOf(imp) < 0) {
                    relations.push(imp);
                    //                    if (imp.usage.indexOf(ref) === -1)
                    //                        imp.usage.push(ref);
                    this.createRelations(imp, relations);
                }
            }
            return relations;
        }

        private createModuleUI(mod: IModule): ModuleUI {
            var ui = new ModuleUI(mod);
            this._content.appendChild(ui.element);
            //            ui.element.addEventListener("click", () => this.onClickElement(ref));
            return ui;
        }

        private createSymbolUI(ref: ISymbol): SymbolUI {
            var ui = new SymbolUI(ref);
            ref.module.ui.list.appendChild(ui.element);
            ui.element.addEventListener("click", () => this.onClickElement(ref));
            return ui;
        }

        private onClickElement(ref: ISymbol): void {
            ref.isChecked = !ref.isChecked;
            this.refreshDependency();
        }

        private refreshDependency(): void {
            //collect checked
            //and reset dependent
            var checked: ISymbol[] = [];
            for (var ref of this.symbols) {
                ref.isDependent = false;
                if (ref.isChecked)
                    checked.push(ref);
            }
            
            //mark dependencies
            for (var ref of checked)
                for (var rel of ref.relations)
                    rel.isDependent = true;
            
            //size
            var size = this.getSize();
            //            console.log("SIZE", text.Text.fileSize(size.bytesMinified));

            //refresh the ui-elements
            this.refreshUI();
        }

        private getSize(): { bytes: number, bytesMinified: number } {
            var files: IFile[] = [];
            for (var ref of this.symbols)
                if (ref.isDependent)
                    if (files.indexOf(ref.file) === -1)
                        files.push(ref.file);
            var bytesMinified = 0;
            var bytes = 0;
            for (var file of files) {
                bytes += file.size;
                bytesMinified += file.sizeMinified;
            }
            return { bytes: bytes, bytesMinified: bytesMinified };
        }

        public sort(f: SortFunction): void {
            this.symbols.sort(f);
            
            //            var display = this._list.style.display;
            //            this._list.style.display = "none";
            //            for (var symbol of this.symbols)
            //                this._list.appendChild(symbol.ui.getElement());
            //            this._list.style.display = display;
        }

        private refreshUI(): void {
            for (var ref of this.symbols) {
                ref.ui.setChecked(ref.isChecked);
                ref.ui.setDependent(ref.isDependent);
            }
        }
    }
}

namespace jsidea.plugins {
    export class Plugin extends jsidea.events.EventDispatcher {
        private _active: boolean = false;
        private _autoActive: boolean = false;
        private _autoTick: boolean = false;
        private _frameRate: number = 60;
        private _tickInterval: number = 0;

        constructor() {
            super();

            this.autoActive = true;
            this.autoTick = true;
            this.tick();
        }

        public get active(): boolean {
            return this._active;
        }

        public set active(value: boolean) {
            if (this._active == value)
                return;
            this._active = value;
        }

        public get autoActive(): boolean {
            return this._autoActive;
        }

        public set autoActive(value: boolean) {
            if (this._autoActive == value)
                return;
            this._autoActive = value;

            if (this._autoActive) {
                this._active = document.visibilityState == "visible";
                document.addEventListener("visibilitychange", () => this.onVisibilityChange());
            }
            else
                document.removeEventListener("visibilitychange", null);
        }

        public get frameRate(): number {
            return this._frameRate;
        }

        public set frameRate(value: number) {
            value = value < 0 ? 0 : value;
            if (this._frameRate == value)
                return;
            this._frameRate = value;
            this.refreshTickInterval();
        }

        public tick(): void {
            //            this.broadcast(jsidea.events.Event.TICK);
            var evt = document.createEvent("Event");
            evt.initEvent("tick", true, true);
            document.dispatchEvent(evt);
        }

        public get autoTick(): boolean {
            return this._autoTick;
        }

        public set autoTick(value: boolean) {
            if (this._autoTick == value)
                return;
            this._autoTick = value;
            this.refreshTickInterval();
        }

        private onVisibilityChange(): void {
            this.active = document.visibilityState == "visible";
        }

        private refreshTickInterval(): void {
            clearInterval(this._tickInterval);
            if (this._autoTick && this._frameRate > 0)
                this._tickInterval = <any> setInterval(() => this.tick(), 1000 / this._frameRate);
        }

        public dispose(): void {
            super.dispose();
        }
    }
}

namespace jsidea.plugins {
    export function Runner(element: HTMLElement): void {
            var plugin: string = element.getAttribute("data-plugin")
            if (!plugin) {
                return;
            }
            var qualifiedClassName = "jsidea.plugins." + plugin;
            var path: string[] = qualifiedClassName.split(".");
            var hook: any = window[<any>path[0]];
            for (var i = 1; i < path.length; ++i) {
                if (!hook) {
                    console.warn("Plugin '" + qualifiedClassName + "' is undefined.");
                    return;
                }

                hook = hook[path[i]];
            }
            if (!hook) {
                console.warn("Plugin '" + qualifiedClassName + "' is undefined.");
                return;
            }
            var app = new hook();
        }
}
//hook
document.addEventListener("DOMContentLoaded", () => {
    jsidea.plugins.Runner(document.body);
});

namespace jsidea.plugins {
    interface ISimboxData {
        active: boolean;
        camDynamicHandle: number[];
        camDynamicTip: number[];
        camEgo: number[];
        camStatic: number[];
        path: number[];
        positionHandle: number[];
        positionInjection: number[];
        positionNearest: number[];
        positionNext: number[];
        positionTip: number[];
        progress: number;
        transformNeedle: number[];
    }
    export class Simbox extends Plugin {
        
        //model/data stuff
        private _host: string = "ws://192.168.1.8:9989";
        private _socket: WebSocket;
        private _data: ISimboxData = null;
        
        //actual data representation
        private _camera: geom.Matrix3D = new geom.Matrix3D();
        private _tip: geom.Point3D = new geom.Point3D();
        private _handle: geom.Point3D = new geom.Point3D();

        //dom-elements
        private _crosshair: HTMLElement;
        private _tipElement: HTMLElement;
        private _handleElement: HTMLElement;
        private _cameraSelect: HTMLSelectElement;
        private _tipSelect: HTMLSelectElement;
        private _handleSelect: HTMLSelectElement;
        private _scale: geom.Point3D = new geom.Point3D(2, 2, 2);

        constructor() {
            super();

            this._crosshair = document.getElementById("crosshair");
            this._tipElement = document.getElementById("tip");
            this._handleElement = document.getElementById("handle");
            this._cameraSelect = <HTMLSelectElement>document.getElementById("camera-select");
            this._tipSelect = <HTMLSelectElement>document.getElementById("tip-select");
            this._handleSelect = <HTMLSelectElement>document.getElementById("handle-select");

            this._socket = new WebSocket(this._host);
            this._socket.onopen = () => console.log("OPEN");
            this._socket.onerror = (e) => console.log("ERROR", e);
            this._socket.onmessage = (m) => this.setData(JSON.parse(m.data));
            this._socket.onclose = () => console.log("CLOSE");

            //TODO: using an array/jquery or something else here
            //or is the event bubbling????
            //            this._cameraSelect.onchange = () => this.refresh();
            //            this._cameraSelect.onkeyup = () => this.refresh();
            //            this._tipSelect.onchange = () => this.refresh();
            //            this._tipSelect.onkeyup = () => this.refresh();
            //            this._handleSelect.onchange = () => this.refresh();
            //            this._handleSelect.onkeyup = () => this.refresh();
            document.documentElement.addEventListener("keyup", () => this.refresh());
            document.documentElement.addEventListener("change", () => this.refresh());
        }

        private refresh(): void {
            if (!this._data)
                return;
            this.setData(this._data);
        }

        private setData(data: ISimboxData): void {
            //set for possible reseting
            this._data = data;
            
            //camera matrix
            var camSelect = this._cameraSelect;
            var opt: HTMLOptionElement = camSelect.options[camSelect.selectedIndex];
            var mat: number[] = (<any>data)[opt.value];
            this._camera.setData(mat);
            
            //tip position
            var tipSelect = this._tipSelect;
            var opt: HTMLOptionElement = tipSelect.options[tipSelect.selectedIndex];
            var mat: number[] = (<any>data)[opt.value];
            this._tip.setData(mat);
            this._camera.transform(this._tip, this._tip);
            
            //handle position
            var handleSelect = this._handleSelect;
            var opt: HTMLOptionElement = handleSelect.options[handleSelect.selectedIndex];
            var mat: number[] = (<any>data)[opt.value];
            this._handle.setData(mat);
            this._camera.transform(this._handle, this._handle);

            //update the dom-elements layout
            this.layout();
        }

        private layout(): void {
            //the center of the crosshair
            var cenX: number = this._crosshair.offsetWidth * 0.5;
            var cenY: number = this._crosshair.offsetHeight * 0.5;

            var offX: number = this._tipElement.offsetWidth * -0.5 + cenX;
            var offY: number = this._tipElement.offsetHeight * -0.5 + cenY;
            offX -= this._tip.x * this._scale.x;
            offY -= this._tip.y * this._scale.y;
            this._tipElement.style.transform = "translate(" + offX + "px, " + offY + "px)";

            offX = this._handleElement.offsetWidth * -0.5 + cenX;
            offY = this._handleElement.offsetHeight * -0.5 + cenY;
            offX -= this._handle.x * this._scale.x;
            offY -= this._handle.y * this._scale.y;
            this._handleElement.style.transform = "translate(" + offX + "px, " + offY + "px)";
        }
    }
}

